mapper.select:
select custno, custname, phone, address, joindate, grade, city
from membertbl 
where custno=#{custno}

mapper.update:
update membertbl 
set custno=#{custno}, custname=#{custname}, phone=#{phone}, address=#{address}, 
	joindate=#{joindate}, grade=#{grade}, city=#{city} 
where custno=#{custno}
